INSERT INTO `category` VALUES (1,'ศูนย์ประชาบดี','1300','เป็นกระทรวงหลักในการขับเคลื่อนการพัฒนาสังคม เพื่อนำไปสู่สังคมที่ดีงาม และอยู่เย็นเป็นสุขร่วมกัน ทุกปัญหามีทางออกบอก ศูนย์ประชาบดี โทร 1300  สายด่วน 24 ชั่วโมง','',1),(2,'ศูนย์ปลอดภัยคมนาคม','1356','ปฏิบัติหน้าที่เป็นศูนย์ประสานภารกิจด้านความปลอดภัยในทุกระบบการขนส่ง รับเรื่องร้องเรียน สายด่วน 1356',NULL,1),(3,'หน่วยแพทย์กู้ชีวิต วชิรพยาบาล	','1554','หน่วยแพทย์กู้ชีวิตวชิรพยาบาล ก่อตั้งขึ้น เมื่อวันที่ 10 ตุลาคมพ.ศ 2537 โดย นายแพทย์ชัยวัน เจริญโชคทวี เป็นผู้ก่อตั้ง \nหน่วยแพทย์กู้ชีวิต วชิรพยาบาล ให้บริการประชาชนเริ่มแรกโดยการช่วยเหลือ ปฐมพยาบาล ณ.จุดเกิด\n โดยการประสาน จากศูนย์ปฏิบัติการ ที่ตั้งอยู่ในโรงพยาบาลวชิรพยาบาล เบอร์โทรศัพท์สายด่วน 1554 เมื่อได้รับแจ้งเหตุ จะจัดรถพยาบาล พร้อมบุคลากรทางการแพทย์ เข้าช่วยเหลือ ณ.ที่เกิดเหตุ พร้อมกับอาสาสมัคร หน่วยแพทย์กู้ชีวิต ในเขตพื้นที่ต่างๆเข้าร่วมให้การช่วยเหลือโดย สื่อสารผ่านทางวิทยุสื่อสาร\nหน่วยแพทย์กู้ชีวิตวชิรพยาบาล ให้การช่วยเหลือ ทั้งทางบกและทางน้ำ โดยทางบก จะจัดรถพยาบาลเข้าให้การช่วยเหลือ ส่วนทางน้ำจะจัดเรือพยาบาล เข้าให้การช่วยเหลือ นอกจากนั้น ในงานสาธารณภัย ก็จะจัดทีม ชุดอุปกรณ์ เข้าทำการสนับสนุนณ.ที่เกิดเหตุด้วย',NULL,1),(4,'หน่วยแพทย์กู้ชีพกรุงเทพมหานคร','1555','หน่วยแพทย์กู้ชีพกรุงเทพมหานคร 1555',NULL,1),(5,'ศูนย์เอราวัณ สำนักการแพทย์ กรุงเทพมหานคร','1646','การจัดบริการทางการแพทย์ในภาวะวิกฤตฉุกเฉิน เพื่อให้ความช่วยเหลือแก่ผู้ประสบอุบัติภัยและ ผู้เจ็บป่วยเป็นหนึ่งในนโยบายของผู้ว่าราชการกรุงเทพมหานครจึงได้มีศูนย์บริการการแพทย์ฉุกเฉินกรุงเทพมหานคร(ศูนย์เอราวัณ) สังกัดสำนักการแพทย์ กรุงเทพมหานคร เป็นศูนย์กลางในการประสานความช่วยเหลือด้านการแพทย์ในภาวะฉุกเฉินทุกประเภท รวมทั้งบริการรถพยาบาลฉุกเฉินพร้อมทีมกู้ชีพในระดับต่างๆเพื่อให้ความช่วยเหลือและลำเลียงผู้บาดเจ็บและผู้ป่วยไปยังสถานพยาบาลที่เหมาะสม นอกจากนั้นยังเป็นศูนย์ข้อมูลข่าวสารทางด้านการแพทย์ประเภท Hotline ที่เรียกว่า “สายด่วนกู้ใจ” ซึ่งรับให้คำปรึกษา ปัญหา คลายเครียด บริการตลอด 24 ชั่วโมง',NULL,1),(6,'ศูนย์สนับสนุนการปฏิบัติการฉุกเฉินสารเคมี','1650','ศูนย์สนับสนุนการปฏิบัติการฉุกเฉินด้านสารเคมี ได้จัดทำสา